import React from "react";
import { getAuth, GoogleAuthProvider, signInWithPopup } from "firebase/auth";
import "./Login.css";

const Login = () => {
  const handleGoogleLogin = async () => {
    const provider = new GoogleAuthProvider();
    const auth = getAuth();

    try {
      console.log("üöÄ Trying Google login...");
      const result = await signInWithPopup(auth, provider);
      const user = result.user;
      console.log("‚úÖ Logged in:", user.displayName, user.email);
      // Redirect or trigger state update here
    } catch (err) {
      console.error("‚ùå Login failed:", err.code, err.message);
      alert(`Login failed: ${err.message}`);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h1>Welcome Back</h1>
        <p className="tagline">Your health records, simplified and secure.</p>
        <button className="google-login-btn" onClick={handleGoogleLogin}>
          <img src="/google-icon.svg" alt="Google icon" />
          Sign in with Google
        </button>
      </div>
    </div>
  );
};

export default Login;
