// src/components/UploadForm/UploadForm.jsx
import React, { useState } from "react";
import { getAuth } from "firebase/auth";
import axios from "axios";

const UploadForm = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [reportType, setReportType] = useState("");
  const [uploading, setUploading] = useState(false);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedFile || !reportType) {
      alert("Please select a file and report type.");
      return;
    }

    const auth = getAuth();
    const user = auth.currentUser;

    if (!user) {
      alert("You must be logged in to upload a report.");
      return;
    }

    try {
      const idToken = await user.getIdToken();
      const formData = new FormData();
      formData.append("original_file", selectedFile);
      formData.append("report_type", reportType);

      setUploading(true);

      const response = await axios.post(
        "http://localhost:8000/api/upload-lab/",
        formData,
        {
          headers: {
            Authorization: `Bearer ${idToken}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );

      alert("‚úÖ Report uploaded and processed successfully!");
      console.log("Response:", response.data);
      setSelectedFile(null);
      setReportType("");
    } catch (error) {
      console.error("Upload error:", error);
      alert("‚ùå Upload failed. Please try again.");
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="upload-form" style={{ padding: "20px" }}>
      <h2>üì§ Upload Lab Report</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Select Report Type:</label>
          <input
            type="text"
            value={reportType}
            onChange={(e) => setReportType(e.target.value)}
            placeholder="e.g. Blood Test, MRI, CT Scan"
            required
          />
        </div>

        <div style={{ marginTop: "10px" }}>
          <label>Choose File:</label>
          <input type="file" accept="image/*,.pdf" onChange={handleFileChange} required />
        </div>

        <button
          type="submit"
          style={{
            marginTop: "20px",
            padding: "10px 20px",
            backgroundColor: uploading ? "#ccc" : "#4caf50",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: uploading ? "not-allowed" : "pointer",
          }}
          disabled={uploading}
        >
          {uploading ? "Uploading..." : "Upload Report"}
        </button>
      </form>
    </div>
  );
};

export default UploadForm;
