import React, { useState } from 'react';
import './ReportModal.css';

const ReportModal = ({ isOpen, onClose, imageUrl, reportData }) => {
  const [imageError, setImageError] = useState(false);
  const [imageLoading, setImageLoading] = useState(true);

  if (!isOpen) return null;

  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const handleImageLoad = () => {
    setImageLoading(false);
    setImageError(false);
  };

  const handleImageError = () => {
    setImageLoading(false);
    setImageError(true);
  };

  // Determine which URL to use - prioritize imageUrl prop, then reportData.file
  const displayUrl = imageUrl || reportData?.file || reportData?.file_url;

  return (
    <div className="modal-overlay" onClick={handleOverlayClick}>
      <div className="modal-content">
        <div className="modal-header">
          <h3>Medical Report</h3>
          <button className="close-button" onClick={onClose}>
            &times;
          </button>
        </div>
        <div className="modal-body">
          {displayUrl ? (
            <div className="image-container">
              {imageLoading && (
                <div className="loading-spinner">
                  <p>Loading image...</p>
                </div>
              )}
              
              {imageError ? (
                <div className="error-message">
                  <p>❌ Error loading image</p>
                  <p>URL: {displayUrl}</p>
                  <button 
                    onClick={() => {
                      setImageError(false);
                      setImageLoading(true);
                    }}
                    className="retry-button"
                  >
                    🔄 Retry
                  </button>
                </div>
              ) : (
                <img
                  src={displayUrl}
                  alt="Medical Report"
                  className="report-image"
                  onLoad={handleImageLoad}
                  onError={handleImageError}
                  style={{ display: imageLoading ? 'none' : 'block' }}
                />
              )}
              
              {/* Show additional report info if available */}
              {reportData && (
                <div className="report-info">
                  {reportData.report_type && (
                    <p><strong>Type:</strong> {reportData.report_type}</p>
                  )}
                  {reportData.report_date && (
                    <p><strong>Date:</strong> {reportData.report_date}</p>
                  )}
                  {reportData.ai_summary && (
                    <div className="ai-summary">
                      <h4>AI Summary:</h4>
                      <p>{reportData.ai_summary}</p>
                    </div>
                  )}
                </div>
              )}
            </div>
          ) : (
            <div className="no-image">
              <p>❌ No image available</p>
              <p>No image URL provided</p>
            </div>
          )}
        </div>
        
        <div className="modal-footer">
          <button onClick={onClose} className="close-modal-btn">
            Close
          </button>
          {displayUrl && (
            <a 
              href={displayUrl} 
              target="_blank" 
              rel="noopener noreferrer"
              className="open-new-tab-btn"
            >
              🔗 Open in New Tab
            </a>
          )}
        </div>
      </div>
    </div>
  );
};

export default ReportModal;
