import React from "react";
import { FaTimes } from "react-icons/fa";
import "./SummaryModal.css";

const SummaryModal = ({ isOpen, onClose, summary, reportType, reportDate }) => {
  if (!isOpen) return null;

  return (
    <div className="summary-modal-overlay" onClick={onClose}>
      <div className="summary-modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="summary-modal-header">
          <h3>AI Summary</h3>
          <button className="close-btn" onClick={onClose}>
            <FaTimes />
          </button>
        </div>
        
        <div className="summary-modal-body">
          <div className="summary-meta">
            <span className="report-type-tag">{reportType}</span>
            <span className="report-date">{reportDate}</span>
          </div>
          
          <div className="summary-content">
            {summary ? (
              <p>{summary}</p>
            ) : (
              <p className="no-summary">No summary available for this report.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SummaryModal;
