import React, { useState, useEffect } from 'react';
import './ReportModal.css';

const ReportModal = ({ isOpen, onClose, imageUrl, reportData }) => {
  const [imageError, setImageError] = useState(false);
  const [imageLoading, setImageLoading] = useState(true);

  // Reset states when modal opens/closes
  useEffect(() => {
    if (isOpen) {
      setImageError(false);
      setImageLoading(true);
    }
  }, [isOpen]);

  // 🔧 FIXED: Handle keyboard events (ESC to close)
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'Escape' && isOpen) {
        onClose();
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleKeyDown);
      document.body.style.overflow = 'hidden'; // Prevent background scroll
    }

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
      document.body.style.overflow = 'unset'; // Restore scroll
    };
  }, [isOpen, onClose]);

  if (!isOpen) return null;

  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const handleImageLoad = () => {
    console.log("✅ Image loaded successfully");
    setImageLoading(false);
    setImageError(false);
  };

  const handleImageError = (e) => {
    console.log("❌ Image failed to load:", e.target.src);
    setImageLoading(false);
    setImageError(true);
  };

  // 🔧 FIXED: Better URL determination with debugging
  const getImageUrl = () => {
    console.log("🔍 Debug - imageUrl prop:", imageUrl);
    console.log("🔍 Debug - reportData:", reportData);

    // Try different possible URL sources
    const possibleUrls = [
      imageUrl,
      reportData?.file,
      reportData?.file_url,
      reportData?.image_url,
      reportData?.url
    ];

    console.log("🔍 All possible URLs:", possibleUrls);

    for (const url of possibleUrls) {
      if (url && 
          url !== 'undefined' && 
          url !== undefined && 
          url.trim() !== '' &&
          !url.includes('/undefined') &&  // 🔧 ADDED: Check for /undefined in URL
          url.startsWith('http')) {  // 🔧 ADDED: Ensure it's a proper URL
        console.log("✅ Using URL:", url);
        return url;
      }
    }

    console.log("❌ No valid URL found - all URLs contained 'undefined' or were invalid");
    return null;
  };

  const displayUrl = getImageUrl();

  const handleRetry = () => {
    console.log("🔄 Retrying image load...");
    setImageError(false);
    setImageLoading(true);
  };

  return (
    <div className="modal-overlay" onClick={handleOverlayClick}>
      <div className="modal-content">
        <div className="modal-header">
          <h3>Medical Report</h3>
          <button 
            className="close-button" 
            onClick={onClose}
            aria-label="Close modal"
          >
            &times;
          </button>
        </div>

        <div className="modal-body">
          {/* 🔧 DEBUGGING INFO (remove in production) */}
          {process.env.NODE_ENV === 'development' && (
            <div className="debug-info">
              <details style={{marginBottom: '20px', padding: '10px', background: '#f0f0f0', borderRadius: '4px'}}>
                <summary style={{cursor: 'pointer', fontWeight: 'bold'}}>🐛 Debug Info (click to expand)</summary>
                <div style={{marginTop: '10px', fontSize: '12px'}}>
                  <p><strong>imageUrl prop:</strong> <code>{String(imageUrl)}</code></p>
                  <p><strong>reportData file:</strong> <code>{String(reportData?.file)}</code></p>
                  <p><strong>reportData file_url:</strong> <code>{String(reportData?.file_url)}</code></p>
                  <p><strong>reportData keys:</strong> {reportData ? Object.keys(reportData).join(', ') : 'No reportData'}</p>
                  <p><strong>Final displayUrl:</strong> <code>{String(displayUrl)}</code></p>
                  <p><strong>Image Error:</strong> {String(imageError)}</p>
                  <p><strong>Image Loading:</strong> {String(imageLoading)}</p>
                  
                  {reportData && (
                    <details style={{marginTop: '10px'}}>
                      <summary>Full reportData object:</summary>
                      <pre style={{fontSize: '10px', overflow: 'auto', background: 'white', padding: '5px', marginTop: '5px'}}>
                        {JSON.stringify(reportData, null, 2)}
                      </pre>
                    </details>
                  )}
                </div>
              </details>
            </div>
          )}

          {displayUrl ? (
            <div className="image-container">
              {/* Loading Spinner */}
              {imageLoading && !imageError && (
                <div className="loading-spinner">
                  <div className="spinner"></div>
                  <p>Loading image...</p>
                </div>
              )}
              
              {/* Error State */}
              {imageError && (
                <div className="error-message">
                  <h4>❌ Failed to Load Image</h4>
                  <p><strong>URL:</strong> <code>{displayUrl}</code></p>
                  <div className="error-actions">
                    <button onClick={handleRetry} className="retry-button">
                      🔄 Retry
                    </button>
                    <a 
                      href={displayUrl} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="direct-link-button"
                    >
                      🔗 Open Direct Link
                    </a>
                  </div>
                </div>
              )}

              {/* Actual Image */}
              {!imageError && (
                <img
                  src={displayUrl}
                  alt="Medical Report"
                  className="report-image"
                  onLoad={handleImageLoad}
                  onError={handleImageError}
                  style={{ display: imageLoading ? 'none' : 'block' }}
                />
              )}
            </div>
          ) : (
            <div className="no-image">
              <h4>📋 No Image Available</h4>
              <p>No valid image URL was provided for this report.</p>
              
              {/* Show what we got for debugging */}
              {process.env.NODE_ENV === 'development' && (
                <div style={{marginTop: '20px', padding: '10px', background: '#fff3cd', borderRadius: '4px'}}>
                  <p><strong>Received data:</strong></p>
                  <pre style={{fontSize: '10px', overflow: 'auto'}}>
                    {JSON.stringify({imageUrl, reportData}, null, 2)}
                  </pre>
                </div>
              )}
            </div>
          )}

          {/* Report Information */}
          {reportData && (
            <div className="report-info">
              <h4>📋 Report Details</h4>
              
              {reportData.report_type && (
                <p><strong>Type:</strong> {reportData.report_type}</p>
              )}
              
              {reportData.report_date && (
                <p><strong>Date:</strong> {new Date(reportData.report_date).toLocaleDateString()}</p>
              )}
              
              {reportData.created_at && (
                <p><strong>Uploaded:</strong> {new Date(reportData.created_at).toLocaleDateString()}</p>
              )}
              
              {reportData.ai_summary && (
                <div className="ai-summary">
                  <h5>🤖 AI Analysis</h5>
                  <div className="summary-text">
                    {reportData.ai_summary.split('\n').map((line, index) => (
                      <p key={index}>{line}</p>
                    ))}
                  </div>
                </div>
              )}
            </div>
          )}
        </div>

        <div className="modal-footer">
          <button onClick={onClose} className="close-modal-btn">
            ❌ Close
          </button>
          
          {displayUrl && !imageError && (
            <a 
              href={displayUrl} 
              target="_blank" 
              rel="noopener noreferrer"
              className="open-new-tab-btn"
            >
              🔗 Open in New Tab
            </a>
          )}
        </div>
      </div>
    </div>
  );
};

export default ReportModal;
