import React, { useState } from "react";
import SummaryModal from "../SummaryModal/SummaryModal";
import { getAuth } from "firebase/auth";

const UploadForm = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [reportType, setReportType] = useState("");
  const [uploading, setUploading] = useState(false);
  const [dragActive, setDragActive] = useState(false);
  const [summaryModalOpen, setSummaryModalOpen] = useState(false);
  const [summaryData, setSummaryData] = useState({
    summary: "",
    reportType: "",
    reportDate: "",
  });

  const reportTypes = [
    { value: "lab", label: "Lab" },
    { value: "ct", label: "CT" },
    { value: "mri", label: "MRI" }
  ];

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Validate file type
      const allowedTypes = ['application/pdf', 'image/jpeg', 'image/jpg', 'image/png'];
      if (!allowedTypes.includes(file.type)) {
        alert("Please select a valid file type (PDF, JPG, PNG)");
        return;
      }
      
      // Validate file size (10MB limit)
      if (file.size > 10 * 1024 * 1024) {
        alert("File size must be less than 10MB");
        return;
      }
      
      setSelectedFile(file);
    }
  };

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      const file = e.dataTransfer.files[0];
      
      // Validate file type
      const allowedTypes = ['application/pdf', 'image/jpeg', 'image/jpg', 'image/png'];
      if (!allowedTypes.includes(file.type)) {
        alert("Please select a valid file type (PDF, JPG, PNG)");
        return;
      }
      
      // Validate file size (10MB limit)
      if (file.size > 10 * 1024 * 1024) {
        alert("File size must be less than 10MB");
        return;
      }
      
      setSelectedFile(file);
    }
  };

  const handleSubmit = async () => {
    if (!selectedFile || !reportType) {
      alert("Please select a file and report type.");
      return;
    }

    setUploading(true);

    try {
      const formData = new FormData();
      formData.append("original_file", selectedFile);
      formData.append("report_type", reportType);

      const auth = getAuth();
      const user = auth.currentUser;

      if (!user) {
        alert("Please log in to upload reports.");
        setUploading(false);
        return;
      }

      const idToken = await user.getIdToken();

      const response = await fetch("http://localhost:8000/api/reports/", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${idToken}`,
        },
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const report = await response.json();

      setSummaryData({
        summary: report.ai_summary || "No summary available.",
        reportType: report.report_type?.toUpperCase() || reportType.toUpperCase(),
        reportDate: report.report_date || new Date().toISOString().split("T")[0],
      });

      setSummaryModalOpen(true);
      setSelectedFile(null);
      setReportType("");
      
    } catch (error) {
      console.error("Upload error:", error);
      
      // Better error handling
      if (error.name === 'TypeError' && error.message.includes('fetch')) {
        alert("Network error. Please check your connection and try again.");
      } else if (error.message.includes('401')) {
        alert("Authentication failed. Please log in again.");
      } else if (error.message.includes('413')) {
        alert("File too large. Please select a smaller file.");
      } else if (error.message.includes('400')) {
        alert("Invalid request. Please check your file and try again.");
      } else {
        alert("Upload failed. Please try again.");
      }
    } finally {
      setUploading(false);
    }
  };

  return (
    <div style={{ 
      minHeight: '100vh', 
      backgroundColor: '#f9fafb',
      fontFamily: 'system-ui, -apple-system, sans-serif' 
    }}>
      {/* Main Content */}
      <div style={{
        maxWidth: '672px',
        margin: '0 auto',
        padding: '64px 24px 0'
      }}>
        <div style={{ textAlign: 'center', marginBottom: '32px' }}>
          <h1 style={{
            fontSize: '30px',
            fontWeight: '700',
            color: '#111827',
            marginBottom: '16px',
            margin: '0 0 16px 0'
          }}>
            Upload Your Health Report
          </h1>
          <p style={{
            color: '#6b7280',
            fontSize: '16px',
            margin: '0'
          }}>
            Upload your lab reports (like blood tests) to receive an AI-generated summary.
          </p>
        </div>

        <div style={{ display: 'flex', flexDirection: 'column', gap: '24px' }}>
          {/* Report Type Dropdown */}
          <div>
            <label style={{
              display: 'block',
              fontSize: '14px',
              fontWeight: '500',
              color: '#374151',
              marginBottom: '8px'
            }}>
              Select Report Type
            </label>
            <div style={{ position: 'relative' }}>
              <select
                value={reportType}
                onChange={(e) => setReportType(e.target.value)}
                style={{
                  width: '100%',
                  padding: '8px 12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '6px',
                  fontSize: '14px',
                  backgroundColor: 'white',
                  appearance: 'none',
                  cursor: 'pointer',
                  paddingRight: '32px'
                }}
              >
                <option value="">Select Report Type</option>
                {reportTypes.map((type) => (
                  <option key={type.value} value={type.value}>
                    {type.label}
                  </option>
                ))}
              </select>
              <div style={{
                position: 'absolute',
                right: '12px',
                top: '50%',
                transform: 'translateY(-50%)',
                pointerEvents: 'none'
              }}>
                <svg style={{ width: '20px', height: '20px', color: '#9ca3af' }} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
              </div>
            </div>
          </div>

          {/* File Upload Area */}
          <div>
            <div
              style={{
                position: 'relative',
                border: dragActive ? '2px dashed #3b82f6' : '2px dashed #d1d5db',
                borderRadius: '8px',
                padding: '32px',
                textAlign: 'center',
                backgroundColor: dragActive ? '#eff6ff' : 'transparent',
                cursor: 'pointer',
                transition: 'all 0.2s'
              }}
              onDragEnter={handleDrag}
              onDragLeave={handleDrag}
              onDragOver={handleDrag}
              onDrop={handleDrop}
            >
              <input
                type="file"
                accept=".pdf,.jpg,.jpeg,.png"
                onChange={handleFileChange}
                style={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '100%',
                  height: '100%',
                  opacity: 0,
                  cursor: 'pointer'
                }}
              />
              
              {selectedFile ? (
                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '8px' }}>
                  <div style={{
                    width: '48px',
                    height: '48px',
                    backgroundColor: '#dcfce7',
                    borderRadius: '50%',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center'
                  }}>
                    <svg style={{ width: '24px', height: '24px', color: '#16a34a' }} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                    </svg>
                  </div>
                  <p style={{ fontSize: '14px', fontWeight: '500', color: '#111827', margin: '0' }}>
                    {selectedFile.name}
                  </p>
                  <p style={{ fontSize: '12px', color: '#6b7280', margin: '0' }}>
                    {(selectedFile.size / 1024 / 1024).toFixed(2)} MB
                  </p>
                </div>
              ) : (
                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '16px' }}>
                  <div style={{
                    width: '48px',
                    height: '48px',
                    backgroundColor: '#f3f4f6',
                    borderRadius: '50%',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center'
                  }}>
                    <svg style={{ width: '24px', height: '24px', color: '#9ca3af' }} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" />
                    </svg>
                  </div>
                  <div>
                    <p style={{ fontSize: '18px', fontWeight: '500', color: '#111827', margin: '0' }}>
                      Drag and drop your files here
                    </p>
                    <p style={{ fontSize: '14px', color: '#6b7280', margin: '4px 0 0 0' }}>Or</p>
                  </div>
                  <button
                    type="button"
                    style={{
                      display: 'inline-flex',
                      alignItems: 'center',
                      padding: '8px 16px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      fontSize: '14px',
                      fontWeight: '500',
                      color: '#374151',
                      backgroundColor: 'white',
                      cursor: 'pointer',
                      boxShadow: '0 1px 2px 0 rgba(0, 0, 0, 0.05)'
                    }}
                  >
                    Browse Files
                  </button>
                </div>
              )}
            </div>
            
            <p style={{
              marginTop: '8px',
              fontSize: '14px',
              color: '#6b7280',
              textAlign: 'center',
              margin: '8px 0 0 0'
            }}>
              Supported formats: PDF, JPG, PNG. Max file size: 10MB
            </p>
          </div>

          {/* Submit Button */}
          <div style={{ paddingTop: '16px' }}>
            <button
              onClick={handleSubmit}
              disabled={uploading || !selectedFile || !reportType}
              style={{
                width: '100%',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                padding: '12px 16px',
                border: 'none',
                borderRadius: '6px',
                fontSize: '14px',
                fontWeight: '500',
                color: 'white',
                backgroundColor: (uploading || !selectedFile || !reportType) ? '#9ca3af' : '#2563eb',
                cursor: (uploading || !selectedFile || !reportType) ? 'not-allowed' : 'pointer',
                transition: 'background-color 0.2s'
              }}
            >
              {uploading ? (
                <div style={{ display: 'flex', alignItems: 'center' }}>
                  <div style={{
                    width: '16px',
                    height: '16px',
                    border: '2px solid transparent',
                    borderTop: '2px solid white',
                    borderRadius: '50%',
                    animation: 'spin 1s linear infinite',
                    marginRight: '8px'
                  }}></div>
                  Uploading...
                </div>
              ) : (
                'Upload Report'
              )}
            </button>
          </div>
        </div>
        
        <SummaryModal
          isOpen={summaryModalOpen}
          onClose={() => setSummaryModalOpen(false)}
          summary={summaryData.summary}
          reportType={summaryData.reportType}
          reportDate={summaryData.reportDate}
        />
      </div>

      <style>
        {`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}
      </style>
    </div>
  );
};

export default UploadForm;
