import React, { useState } from "react";
import SummaryModal from "./SummaryModal";
import "./UploadForm.css";

const UploadForm = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [reportType, setReportType] = useState("");
  const [uploading, setUploading] = useState(false);
  const [dragActive, setDragActive] = useState(false);
  
  // Modal state
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [summary, setSummary] = useState(null);
  const [reportDate, setReportDate] = useState("");

  const reportTypes = [
    { value: "lab", label: "Lab" },
    { value: "ct", label: "CT" },
    { value: "mri", label: "MRI" }
  ];

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
    }
  };

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      setSelectedFile(e.dataTransfer.files[0]);
    }
  };

  const handleSubmit = () => {
    if (!selectedFile || !reportType) {
      alert("Please select a file and report type.");
      return;
    }

    setUploading(true);

    // Simulate upload process
    setTimeout(() => {
      // Set report date and pass summary as null (let SummaryModal handle it)
      setReportDate(new Date().toLocaleDateString());
      setSummary(null);
      
      // Reset form
      setSelectedFile(null);
      setReportType("");
      setUploading(false);
      
      // Open modal
      setIsModalOpen(true);
    }, 2000);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSummary(null);
    setReportDate("");
  };

  return (
    <div className="upload-form-container">
      <div className="upload-form-content">
        <div className="upload-form-header">
          <h1 className="upload-form-title">
            Upload Your Health Report
          </h1>
          <p className="upload-form-description">
            Upload your lab reports (like blood tests) to receive an AI-generated summary.
          </p>
        </div>

        <div className="upload-form-fields">
          {/* Report Type Dropdown */}
          <div className="field-group">
            <label className="field-label">
              Select Report Type
            </label>
            <div className="select-wrapper">
              <select
                value={reportType}
                onChange={(e) => setReportType(e.target.value)}
                className="select-field"
              >
                <option value="">Select Report Type</option>
                {reportTypes.map((type) => (
                  <option key={type.value} value={type.value}>
                    {type.label}
                  </option>
                ))}
              </select>
              <div className="select-arrow">
                <svg className="select-arrow-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
              </div>
            </div>
          </div>

          {/* File Upload Area */}
          <div className="upload-area-wrapper">
            <div
              className={`upload-area ${dragActive ? 'drag-active' : ''}`}
              onDragEnter={handleDrag}
              onDragLeave={handleDrag}
              onDragOver={handleDrag}
              onDrop={handleDrop}
            >
              <input
                type="file"
                accept=".pdf,.jpg,.jpeg,.png"
                onChange={handleFileChange}
                className="file-input"
              />
              
              {selectedFile ? (
                <div className="file-selected">
                  <div className="file-icon-success">
                    <svg className="success-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                    </svg>
                  </div>
                  <p className="file-name">
                    {selectedFile.name}
                  </p>
                  <p className="file-size">
                    {(selectedFile.size / 1024 / 1024).toFixed(2)} MB
                  </p>
                </div>
              ) : (
                <div className="upload-placeholder">
                  <div className="upload-icon">
                    <svg className="upload-icon-svg" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" />
                    </svg>
                  </div>
                  <div className="upload-text">
                    <p className="upload-main-text">
                      Drag and drop your files here
                    </p>
                    <p className="upload-or-text">Or</p>
                  </div>
                  <button type="button" className="browse-button">
                    Browse Files
                  </button>
                </div>
              )}
            </div>
            
            <p className="upload-info">
              Supported formats: PDF, JPG, PNG. Max file size: 10MB
            </p>
          </div>

          {/* Submit Button */}
          <div className="submit-section">
            <button
              onClick={handleSubmit}
              disabled={uploading || !selectedFile || !reportType}
              className={`submit-button ${(uploading || !selectedFile || !reportType) ? 'disabled' : ''}`}
            >
              {uploading ? (
                <div className="loading-content">
                  <div className="spinner"></div>
                  Uploading...
                </div>
              ) : (
                'Upload Report'
              )}
            </button>
          </div>
        </div>
      </div>

      {/* Summary Modal */}
      <SummaryModal
        isOpen={isModalOpen}
        onClose={handleCloseModal}
        summary={summary}
        reportType={reportType}
        reportDate={reportDate}
      />
    </div>
  );
};

export default UploadForm;
